INSERT INTO `t_sight` VALUES (85, '带龙桥', '1830246', '地址：贺州市昭平县黄姚镇黄姚街（黄姚古镇正门对面）', '包含于 黄姚古镇景区，景点开放时间详见景区现场公示。', '4.4', ' 是黄姚古镇15座古桥梁中最大的阶梯石拱桥。始建于明朝万历三年，清乾隆二十三年（1759年）重修。该桥长22m，宽3m，有两拱，一大一小，大拱离水面3.2m，直径约5.6m；小拱是旱拱，离地面2.55m,直径约3m。 ', 'hezhou707', '', ' 0774-6722748 ', '');
