INSERT INTO `t_sight` VALUES (264, '大龙潭水库', '1708676', '地址：百色市靖西县城东北1公里许环河村东侧石山脚下', ' ', '4.5', ' 大龙潭，位于广西壮族自治区百色市靖西县城东北1公里许环河村渔翁撒网东侧石山脚下， 为龙潭河源头处，潭为一口上升泉，系一条大的地下河出口，水深莫测，泉水上涌，成一湖，曾名安湖。宽20多亩，古传水深有龙藏，故名大龙潭，为靖西八景之一。 ', 'jingxi967', '', '', '');
INSERT INTO `t_sight` VALUES (265, '靖西三牙山', '70462', '地址：百色市靖西县承德村', '9:30-18:00', '5', ' 靖西三牙山位于广西百色市靖西县城西3千米处。是一片由碳酸岩构成的岩溶山原地貌带，自然景观奇特，岩溶地穴丰富，变幻无穷，充满着神奇的奥秘和多彩多姿的景色，整个风景区面积近千亩。 ', 'jingxi967', '', '', '');
INSERT INTO `t_sight` VALUES (266, '念八峡', '4688345', '地址：百色市靖西县湖润镇新灵村通灵大峡谷景区内', '包含于通灵大峡谷景区，景区开放时间冬令8:00-17:00，夏令8:00-17:30，景点开放时间详见景区现场公示。', '4.7', ' 念八峡又称为公峡，是通灵大峡谷核心景点之一，念八峡与通灵峡谷连为一体，开阔明朗，山光水色交相晖映。游客可以在观赏完单级落差达188米的通灵瀑布后，再穿过念八溶洞，观赏旖旎的念八峡风光。 ', 'jingxi967', '', ' 0776-6180076 ', '');
INSERT INTO `t_sight` VALUES (267, '悬棺洞葬', '4689347', '地址：百色市靖西县湖润镇新灵村通灵大峡谷景区内', '8:00-17:00', '4', ' 通灵大峡谷的奇特景观之一，就是“古悬洞葬”。“古悬洞葬”又名崖葬，即在峭壁悬崖寻找洞穴，将棺柩安置其中。 ', 'jingxi967', '', ' 0776-6180076 ', '');
INSERT INTO `t_sight` VALUES (268, '靖西', '4993341', '地址：广西省百色市靖西市', ' ', '5', ' 靖西位于广西南部的中越边境，是典型的壮族聚居县。这里以溶蚀高原地貌为主，可以欣赏到奇峰异洞和四季如春的自然风光。主要景点包括通灵大峡谷、鹅泉、古龙山峡谷群等。 ', 'jingxi967', '', '', '');
INSERT INTO `t_sight` VALUES (269, '壮灵洞', '4683345', '地址：色市靖西县湖润镇新灵村通灵大峡谷景区内', '包含于通灵大峡谷景区，景区开放时间夏令8:00-17:30，冬令8:00-17:00，景点开放时间详见景区现场公示。', '4', ' 壮灵洞位于通灵大峡谷内，壮灵洞洞口又宽又大，古藤老树盘根错节的生长，树木掩映着洞外的蓝天白云，洞中所包含的山水，显露出一种气吞山河的非凡气势，称之为“气吞山河”。 ', 'jingxi967', '', ' 0776-6180076 ', '');
