INSERT INTO `t_sight` VALUES (291, '霞义山', '5382361', '地址：南宁市横县', ' ', ' ', ' 霞义山位于横县西部、南宁东部，风景靓丽、视野壮阔。在山顶可以看到壮观的白色风车群，还可俯瞰整个六景小镇。据说每年初夏时节，这里总会出现漫天飞舞的萤火虫，在夜幕中闪闪发光，如梦一般，很是神秘。此外，这里山形规整，常年风力持续稳定，高度适宜，是天然的滑翔场地。目前，霞义山的六景滑翔伞基地已建成滑翔伞起飞降落场、基地接待中心等基础设施，使之成为广西滑翔伞爱好者飞行的乐园。 ', 'hengxian966', '', '', '');
INSERT INTO `t_sight` VALUES (292, '仁和村', '2499768', '地址：横县六景镇仁和村', '全天开放', ' ', ' 仁和村于2013年11月确定为南宁市综合示范村，村名寓意：仁德故里，和乐之乡。六景镇仁和村，可见古色古香的牌楼竖立村口，与青砖灰瓦的民居楼巧妙融合。村内潺潺的流水与笔直的村道交相辉映，葱茏的绿树与清新的空气相得益彰，田野上金灿灿的稻谷与生态农业示范园里的瓜果飘香勾勒出一幅村容整洁、生活幸福的美丽乡村画卷。 ', 'hengxian966', '', ' 18977124121 ', '');
