INSERT INTO `t_sight` VALUES (398, '岭祖大峡谷', '4239758', '来宾市金秀瑶族自治县来宾忠良乡三合村二屯竹甲桥头', '8:30-17:00', ' ', '[岭祖大峡谷景区生态环境自然清幽，森林景观苍翠原始，气候舒适宜人，年均气温17oC，冬暖夏凉。, 是个融赏山乐水、养生度假、体验高山部落生活、进行高山滑翔活动于一体的浪漫处所。, 峡谷内山峻岩险，溪中潭深流急，怪石遍布，飞瀑婀娜，是一处沉睡千年的绿谷，一处充满原始野性的好地方。]', 'jinxiu1445186', '', '', '');
