INSERT INTO `t_sight` VALUES (495, '山湖秀色', '57784', '地址：广西梧州市苍梧县县城北4公里处', ' ', ' ', ' 山湖秀色胜景位于城北四公里处，为蒙山新辟的一个游览区。这个茶山河建起的茶山水库，纵深十四公里，是农田灌溉，发电，游览综合性的高山湖泊。茶山口宏伟的水库大坝两侧，像雄狮的山峰，遥遥相望，自古称此景为“双狮把塞口”。泛舟湖上，两岸奇峰罗列，青山倒影，鸟语花香，人在画中游。峰回水转，可观赏到那神态端庄，栩栩如生的古代妇女形象的石人山，以及“白马饮泉”、“金鸡啼岸”、“石鼓浮星”等胜景，增加无穷的情趣。 ', 'mengshan3057', '', '', '');
