INSERT INTO `t_sight` VALUES (573, '德天老木棉景区', '1933086', '地址：崇左市大新县硕龙隘江村', '8:00-16:00', '4.4', ' 老木棉·紫园景区位于广西壮族自治区崇左市大新县硕龙镇隘江村，景区座落于中越界河——归春河畔，归春河支流如网状流经景区，这里四季流水潺潺，凤竹生幽，木棉紫藤遇春天染红紫。景区以紫藤园林为核心，由紫藤园林展示区、传统雕刻技艺展示区、盆景奇石艺术区、东盟文化艺术交流区五大特色区域组成。 ', 'daxin963', '', '', '');
INSERT INTO `t_sight` VALUES (574, '骆越自驾游营地', '5649369', '地址：崇左市大新县雷平镇安平村', '9:00-18:00', '1', ' 骆越自驾游营地位于广西崇左市大新县雷平镇安平仙河景区内，是安平仙河景区具有民族特色的民宿营地酒店。营地主要以别具特色的壮乡民宿客房、营地房车、露营野奢帐篷为主，同时拥有景色如画的户外休闲泳池、别具当地特色的中式餐厅、会议客厅、全地形越野车及休闲自行车等配套设施。 ', 'daxin963', '', '', '');
