INSERT INTO `t_sight` VALUES (677, '神仙湖景区', '4582089', '地址：贺州市富川瑶族自治县203省道', '9:00-22:00', '4', ' 神仙湖景区位于富川瑶族自治县福利镇境内，距县城9公里，坐落在具有古文化风情遗风的八百岭村落边，因乡野情趣浓郁、生态原始古朴、自然风光迷人、乡土气息浓郁而闻名。由健身娱乐区、田园风光游览区、摘果尝鲜生态区、民俗文化展示区等四大生态旅游休闲区组成。神仙湖彩灯将瑶族文化特色、现代卡通动漫等元素结合，打造出一个穿越时空的宏伟壮观场面，加上新颖的互动式体验，形、色、光、声、动的完美结合，让您如同身处玄幻电影大片之中！ ', 'fuchuan1446258', ' AA ', '', '');
