INSERT INTO `t_sight` VALUES (703, '恭城', '1417471', '地址：桂林市恭城瑶族自治县恭城镇', '全天开放', '4.4', ' 恭城处于中国大地构造位置江南古陆南缘，次一级构造海洋山隆起、都庞岭一银殿山隆起、灌阳一恭城地槽的南端。由于地壳多次构造运动的影响，地层时代较多，褶皱、断裂构造发育，火成活动强烈，矿产资源十分丰富。 恭城有桃花节，4月份时去看很美。恭城特产油茶，现擂的油茶爽口提神。 ', 'gongcheng1446252', '', ' 0773-8212371 ', 'http://www.gongcheng.gov.cn/');
